.request {
  &-container {
    .el-input__inner {
      text-transform: capitalize;
    }
    .el-select-dropdown__item {
      text-transform: capitalize;
    }

    margin: 30px;
    .content-input {

      display: flex;

      .title {
        width: 165px;
        display: inline-block;
        color: black;
      }

      .content-input-1 {
        width: 45%;
        .title {
          color: black;
          line-height: 3;
          font-weight: 600;
          font-size: 13px;
          width: 250px;
        }
      }

      .border-bottom {
        border-bottom: 1px solid #dadada;
        margin-bottom: 19px;
        padding-bottom: 15px;
      }

      .content-input-2 {
        width: 45%;
        margin-left: 50px;
        .title {
          color: black;
          line-height: 3;
          font-weight: 600;
          font-size: 13px;
          width: 250px;
        }
      }

      .mt-1 {
        margin-top: 10px;
        display: flex;
      }
      .mt-2 {
        margin-top: 10px;
        display: flex;
      }
      .mt-3 {
        margin-top: 10px;
        display: flex;
      }
    }

    .sort-lecturer {
      display: inline-block;
      margin: 0 20px 30px;
    }

    .pagination-generation {
      text-align: left;
      margin-top: 20px;
      margin-bottom: 20px;
      margin-left: -5px;

      .el-pager {
        li {
          min-width: 34px;
          line-height: 32px;
          height: 32px;
        }
      }

      &.el-pagination button {
        line-height: 32px;
        height: 32px;
      }
    }

    .el-dialog {
      width: 90%;
    }

    .dialog-detail-generation {
      .title {
        color: #0089ff;
        font-weight: 700;
      }
      .el-dialog {
        width: 70%;
      }
    }
    .el-table th {
      background: #f5f5f5;
    }
    .content {
      border: 1px solid #d2d2d2;
      padding: 20px;
      margin-top: 20px;
      margin-bottom: 20px;
      border-radius: 4px;
      box-shadow: 0px 0px 4px -1px #d2d2d2;
      .select-model{
        padding-left: 30px;
      }

      .title {
        color: #0089ff;
        // width: auto;
        // padding-bottom: 40px;
        font-weight: 700;
      }
      .titleRunning {
        font-size: 1.5em;
        color: red;
        width: auto;
        font-weight: 700;
      }
      .titleSuccess {
        font-size: 1.5em;
        color: green;
        width: auto;
        font-weight: 700;
      }
    }

    .type-file {
      display: block;
      margin-top: 10px;
      font-size: 12px;
      color: red;
    }

    .upload-btn-wrapper {
      position: relative;
      overflow: hidden;
      display: inline-block;
    }

    .btn {
      border: 1px solid #cacaca;
      color: gray;
      background-color: white;
      padding: 11px 20px;
      border-radius: 8px;
      font-size: 14px;
    }

    .upload-btn-wrapper input[type=file] {
      font-size: 100px;
      position: absolute;
      left: 0;
      top: 0;
      opacity: 0;
    }

    .name-file {
      margin: 0 20px;
      overflow: hidden;
      text-overflow: ellipsis;
      width: 260px;
    }

    .ml-2 {
      margin-left: 20px;
    }

    &__wrapper {
      display: flex;
      align-items: center;
      margin-top: 20px;
    }
  }
  &-text {
    font-size: 30px;
    line-height: 46px;
  }
  .el-row {
    margin-bottom: 20px;
    &:last-child {
      margin-bottom: 0;
    }
  }
}
.manager-radio {
  .list-radio__main--lever-header{
    width: 68px;
  }

  .list-radio__main--lever-item {
    width: 68px;
  }

  .list-radio__main--lever-wrapper {
    padding-left: 100px
  }

  .list-radio .list-subject-content_name {
    width: 100px;
  }

  &-container {
    margin: 30px;
  }

  &-item-select {
    text-transform: capitalize;
  }

  &-header {
    display: flex;

    .button {
      margin-left: 20px;
      display: inherit;
    }

    .el-input__inner {
      text-transform: capitalize;
    }
  }

  &_content {
    box-shadow: 1px 0px 4px 1px #e4e4e4;
    border: 1px solid #e8e8e8;
    padding: 20px;
    margin-top: 20px;

    &-footer {
      margin-top: 20px;

      &-item {
        display: flex;
        margin-top: 20px;

        .title-item {
          width: 340px;
          display: inline-block;
        }

        .el-textarea {
          width: 400px;
        }
      }
    }
  }
  .reUseButton {
    margin-left: 10px;
  }

  .title {
    color: #00adff;
    text-transform: uppercase;
    font-weight: 600;

  }
  .title-warning {
    color: red;
    text-transform: uppercase;
    font-weight: 500;
    width: 800px;
    display: inline-block;
    padding-top: 20px;
  }

  &_wrap {
    display: flex;
    margin-top: 20px;
  }

  &-text {
    font-size: 30px;
    line-height: 46px;
  }

  &_time {
    width: 50%;

    .list-radio {
      margin-top: 10px;
    }
  }

  &_subject {
    margin-right: 10px;
    width: 50%;

    .list-radio {
      margin-top: 10px;
    }
  }
}

.dashboard-editor-container {
  border: 1px solid #d2d2d2;
  padding: 20px;
  margin-top: 20px;
  border-radius: 4px;
  box-shadow: 0px 0px 4px -1px #d2d2d2;
  position: relative;

  .github-corner {
    position: absolute;
    top: 0px;
    border: 0;
    right: 0;
  }
  .chart-wrapper {
    background: #fff;
    padding: 16px 16px 0;
    margin-bottom: 32px;
  }

  .title {
    color: #0089ff;
    font-weight: 700;
  }

}
@media (max-width:1024px) {
  .chart-wrapper {
    padding: 8px;
  }
}
