$bg:#2d3a4b;
$dark_gray:#889aa4;
$light_gray:#eee;

.login-container {
  min-height: 100%;
  width: 100%;
  overflow: hidden;
  text-align: center;

  .login-form {
    position: relative;
    width: 500px;
    max-width: 100%;
    padding: 15px 15px;
    margin: 150px auto;
    overflow: hidden;
    background-color:rgba(255,255,255,0.8);
    border: 2px solid rgba(255, 255, 255, 0.1);

    .btn-wrapper {
      background: no-repeat;
      border: none;
      width: 100%;

      .abcRioButton {
        padding: 0 8px;
        width: auto !important;
        border-radius: 15px;

        &.abcRioButtonLightBlue {
          background-color: #00adff;
          color: #fff;
        }

        .abcRioButtonContentWrapper {
          display: flex;
          justify-content: center;
        }
      }
    }
  }

  .tips {
    font-size: 14px;
    color: #fff;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .title-container {
    position: relative;

    .title {
      font-size: 26px;
      color: $light_gray;
      margin: 0px auto 40px auto;
      text-align: center;
      font-weight: bold;
    }
  }

  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }
  .logo-titlte {
    font-size: 18px;
    opacity: 0.2;
    font-weight: bold;
    /*color: #00adff;*/
  }
}
