.calendar-view {
  display: block;

  .table-custom {
    font-size: 14px;

    &__main--lever-header {
    }

    &__main--lever-header, &__main--lever-item {
      width: 115px;
    }
  }

  &-content {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    margin-top: 20px;
    padding-top: 160px;

    &-detail {
      width: 30%;
      border: 1px solid #afaeae;
      padding: 20px;
      margin-right: 20px;
      border-radius: 4px;
    }
  }

  &-comment {
    width: 100%;
    text-align: center;
    margin-bottom: 20px;

    span {
      position: relative;

      &::before {

        content: "";
        width: 10px;
        height: 10px;
        position: absolute;
        top: 4px;
        background: green;
        left: -15px;
        border-radius: 50%
      }
    }
  }

  &-item-select {
    text-transform: capitalize;
  }

  &-header {
    text-align: center;
    border: 1px solid #dadada;
    padding-top: 10px;
    border-radius: 15px;
    box-shadow: 0px 0px 5px #dadada;
    background-color: #fff;
    position: fixed;
    top: 50px;
    width: auto;
    margin-right: 20px;

    .el-select {
      margin-bottom: 10px;
      max-height: 70px;
    }

    .el-select__tags {
    max-height: 55px;
      overflow: auto;
    }

    ::-webkit-scrollbar {
      width: 0px;
    }

    .el-input {
      width: 385px;
    }

    .el-input__inner {
      text-transform: capitalize;
    }
  }

  &-edit {
    width: 25%;
    margin-right: 20px;
  }

  &-table {
    display: flex;

    &_content {
      width: 100px;
      border-right: 1px solid #b3b2b2;
      border-top: 1px solid #b3b2b2;
      border-bottom: 1px solid #b3b2b2;

      &:nth-child(1) {
        border-left: 1px solid #b3b2b2;
      }

      .arrange-table_main {
        &:last-child {
          border-bottom: none;
        }

        &:nth-child(odd) {
          background: #ececec;
        }
      }
    }

    &_header {
      border-bottom: 1px solid #b3b2b2;
      width: 100%;
      display: block;
      text-align: center;
      font-weight: 600;
      background: #304156;
      color: #fff;
      padding: 10px;

      .el-input__inner {
        text-transform: capitalize;
      }
    }

    &_main {
      border-bottom: 1px solid #b3b2b2;
      cursor: pointer;
      padding: 10px;
    }
  }
}
