result:
.arrange {
  display: block;

  .table-custom {
    margin-top: 60px;
  }

  .view-all {
    .el-dialog {
      width: 80%;
    }
    .filter_expected{
      margin-bottom: 20px;
    }
  }
  .arrange-content_nameObject{
      display: block;
   }
  .name-subject {
    padding-left: 25px;
  }

  .content-input {
    width: calc(100% - 95px);
  }

  .button {
    margin-top: 15px;

    .el-button {
      width: 100%;
    }
  }

  .title {
    padding-top: 10px;
    margin: 0;
    padding-bottom: 10px;
    border-bottom: 1px solid #949494;
    font-weight: 700;
    text-align: center;
    text-transform: capitalize;
    color: #304156;
  }

  .wrapper-input {
    margin-top: 20px;

    .title-name {
      width: 90px;
      font-weight: 700;
      display: inline-block;
    }
  }

  &-content {
    display: flex;
    margin-top: 180px;
    position: relative;

    &-detail {
      width: 280px;
      border: 1px solid #afaeae;
      padding: 20px;
      margin-right: 20px;
      border-radius: 4px;
      margin-top: -48px;
    }

    &_nameObject {
      font-weight: 700;
      display: inline-block;
      margin-bottom: 0;
    }
  }

  &-item-select {
    text-transform: capitalize;
  }
  .title-warning {
    color: red;
    text-transform: uppercase;
    font-weight: 500;
    width: 800px;
    display: inline-block;
    padding-top: 120px;
  }
  &-header {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    text-align: center;
    border: 1px solid #dadada;
    padding-top: 10px;
    padding-left: 10px;
    border-radius: 15px;
    box-shadow: 0px 0px 5px #dadada;
    background-color: #fff;
    position: fixed;
    top: 50px;
    width: auto;
    margin-right: 20px;
    z-index: 9;

    .el-select {
      margin-bottom: 10px;
      max-height: 70px;
    }

    .el-select__tags {
      max-height: 55px;
      overflow: auto;
    }

    ::-webkit-scrollbar {
      width: 0px;
    }

    .el-select {
      margin-bottom: 10px;
    }

    .el-button {
      margin-left: 20px;
      height: 39px;
    }

    .el-input {
      width: 385px;
    }

    .el-input__inner {
      text-transform: capitalize;
    }
  }

  &-edit {
    width: 25%;
    margin-right: 20px;
  }

  &-table {
    display: flex;
    flex-wrap: wrap;
    width: calc(100% - 280px);
    overflow: auto;
    height: calc(100vh - 290px);
    right: 0;
    position: absolute;
    padding-left: 10px;

    .group-by {
      width: 100%;
      margin-top: 20px;
      margin-bottom: 5px;
      position: fixed;
      top: 180px;
      display: block;

      background: #fff;
      z-index: 999;
      padding-bottom: 10px;
      padding-top: 20px;

      .ml-20 {
        margin-left: 20px;
      }

      .mt-20 {
        margin-top: 20px;
      }

      .wrapper-color {
        display: inline-block;

        .final,
        .public,
        .reject,
        .draft {
          position: relative;
          margin-left: 20px;
          margin-left: 60px;

          &::before {
            content: "";
            position: absolute;
            top: 0;
            width: 20px;
            height: 17px;
            left: -25px;
          }
        }

        .final {
          &::before {
            background-color: #13ce66;

          }
        }

        .public {
          &::before {
            background-color: orange;
          }
        }

        .draft {
          &::before {
            background-color: #409EFF;
          }
        }

        .reject {
          &::before {
            background-color: red;
          }
        }
      }

      .el-input__inner {
        text-transform: capitalize;
      }

      .el-select {
        margin-left: 10px;
      }
    }

    &_content {
      width: 100px;
      border-right: 1px solid #b3b2b2;
      border-top: 1px solid #b3b2b2;
      border-bottom: 1px solid #b3b2b2;

      &:nth-child(1) {
        border-left: 1px solid #b3b2b2;
      }

      .arrange-table_main {
        &:last-child {
          border-bottom: none;
        }

        &:nth-child(odd) {
          background: #ececec;
        }
      }
    }

    &_header {
      border-bottom: 1px solid #b3b2b2;
      width: 100%;
      display: block;
      text-align: center;
      font-weight: 600;
      background: #304156;
      color: #fff;
      padding: 10px;

      .el-input__inner {
        text-transform: capitalize;
      }
    }

    &_main {
      border-bottom: 1px solid #b3b2b2;
      cursor: pointer;
      padding: 10px;
    }
  }
}
